import React, { useEffect } from "react";
export default function Local(props){
    useEffect(() => {
        const data = window.localStorage.getItem('MY_APP_STATE');
        if ( data !== null ) props.setValue(JSON.parse(data));
      }, []);
    useEffect(() => {
        window.localStorage.setItem('MY_APP_STATE', JSON.stringify(props.state));
      }, [props.state]);
    return(
        <></>
    )
}
