import React, { useEffect } from "react";
export default function LocalStorage(props){
    useEffect(() => {
        const data = window.localStorage.getItem('value');
        if ( data !== null ) props.setValues(JSON.parse(data));
      }, []);
    useEffect(() => {
        window.localStorage.setItem('value', JSON.stringify(props.value));
      }, [props.value]);
    return(
        <></>
    )
}
