import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { useForm } from 'react-hook-form'


export default function Step1(){
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const nav=useNavigate();
    const[value,setValue]=useState({firstName:'',lastName:''})
    const handleChange=(e)=>{
        console.log(e.target.value)
        const name=e.target.name
        const val=e.target.value
        setValue((prev)=>{
            return{...prev,[name]:val}
        })
    }
    const onClick=()=>{
        nav('/step2')
    }

    return(
        <div>
            <form className="form" onSubmit={handleSubmit(onClick)}>
                <label>სახელი</label>
                <input
                name="firstName"
                value={value.firstName}
                 type="text" 
                 {...register('firstName',{onChange:(e)=>handleChange(e)})}
                 />
                <label>გვარი</label>
                <input 
                name="lastName"
                value={value.lastName}
                type="text"
                {...register('latstName',{onChange:(e)=>handleChange(e)})}
                />
                <label>პირადი ფოტოს ატვირთვა</label>
                <input 
                type="file" 
                {...register('image',{onChange:(e)=>handleChange(e)})}
                />
                <img 
                src='' 
                alt="" 
                />
                <label>ჩემს შესახებ (არასავალდებულო)</label>
                <input 
                type="textarea" 
                {...register('textarea',{onchange:(e)=>handleChange(e)})}
                />
                <label>ელ-ფოსტა</label>
                <input 
                type="email" 
                {...register('email',{onchange:(e)=>handleChange(e)})}
                />
                <label>მობილურის ნომერი</label>
                <input 
                type="number" 
                {...register('number',{onchange:(e)=>handleChange(e)})}
                />
                <button
                type="submit"
                className="button">შემდეგი</button>
            </form>
            <li>{value.firstName}</li>
            <li>{value.lastName}</li>
            <li></li>
        </div>
    )
}
