import { useState,useEffect} from "react"
import React from "react";
import { useLocation, useNavigate } from "react-router-dom"
import { useForm } from 'react-hook-form'
import Header from "./Header";
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";


export default function Step1(){

    const schema = yup.object().shape({
        firstName: yup.string().required(),
      }).required();
    const { register, handleSubmit,reset, formState: { errors },clearErrors } = useForm({mode: 'all', resolver: yupResolver(schema),});
    const nav=useNavigate();
    const [image, setImage] = useState([]);
    const [imgURL, setImgURL] = useState([]);

     const[value,setValue]=useState({
        firstName:'',
        lastName:'',
        textarea:'',
        email:'',
        number:''
})

    const handleChange=(e)=>{
        const name=e.target.name
        const val=e.target.value
        setValue({...value,[name]:val}
        )
        setImage([...e.target.files])
    }

    useEffect(()=>{
        if(image.length<1)return;
        const newImg=[];
        image.forEach(item=>newImg.push(URL.createObjectURL(item)
        ))
        setImgURL(newImg)
    },[image])

    const onClick=(data)=>{
        nav('/step2',{state:{
            firstName:value.firstName,
            lastName:value.lastName,
            textarea:value.textarea,
            email:value.email,
            number:value.number
        }})
    }

    useEffect(() => {
        const data = window.localStorage.getItem('MY_APP_STATE');
        if ( data !== null ) setValue(JSON.parse(data));
      }, []);
    useEffect(() => {
        window.localStorage.setItem('MY_APP_STATE', JSON.stringify(value));
      }, [value]);

    return(
        <div className="form-div">
            <div className="form-wraper">
            <Header heading='პირადი ინფო' pages='1\3'/>
            <form className="form" onSubmit={handleSubmit(onClick)}>

            <input
                className={` ${errors.firstName?'red':'input-name'}`}
                {...register('name', {required:true})}
              />
            {errors.name?<p style={{color:'red'}}>error</p>:''}

                <div className="input-name-div">
                <div className="namediv">
                <label>სახელი</label>
                <input
                name="firstName"
                placeholder="ანზორ"
                className={`input-name ${errors.firstName?'red':'input-name'}`}
                value={value.firstName}
                 type="text" 
                 {...register('firstName',{required: true,pattern: /^[ა-ჰა-ჰ]+$/i, minLength: 2, onChange:(e)=>handleChange(e) })}
                 />
                {errors.firstName?<p style={{color:'red'}}>error</p>:''}
                 <p className="name-criteria">მინიმუმ 2 ასო, ქართული ასოები</p>
                 </div>
                 <div className="namediv">
                <label>გვარი</label>
                <input 
                placeholder="მუმლაძე"
                className="input-name"
                name="lastName"
                value={value.lastName}
                type="text"
                {...register('lastName',{onChange:(e)=>handleChange(e),pattern: /^[ა-ჰა-ჰ]+$/i, required:true})}
                />
                <p className="name-criteria">მინიმუმ 2 ასო, ქართული ასოები</p>
                </div>
                </div>
                <div className="image-input-div">
                <label className="image-label">პირადი ფოტოს ატვირთვა</label>
                <div onClick={()=>document.querySelector('.image-input').click()}className="image-button">ატვირთვა</div>
                <input 
                className="image-input"
                // onChange={()=>upLoadChange}
                // ref={hiddenFileInput}
                type="file"
                name="image" 
                accept="image/*"
                {...register('image',{onChange:(e)=>handleChange(e)})}
                />
                </div>
                <img 
                src='' 
                alt="" 
                />
                <label className="text-label">ჩემს შესახებ (არასავალდებულო)</label>
                <textarea 
                className="textarea"
                placeholder="ზოგადი ინფო შენს შესახებ"
                name="textarea"
                value={value.textarea}
                {...register('textarea',{onChange:(e)=>handleChange(e)})}
                />
                <label className="email-label">ელ-ფოსტა</label>
                <input 
                placeholder="anzorr666@redberry.ge"
                className="input-email"
                type="email"
                name="email" 
                value={value.email}
                {...register('email',{onChange:(e)=>handleChange(e)})}
                />
                <p className="name-criteria">უნდა მთავრდებოდეს @redberry.ge-ით</p>
                <label className="number-label">მობილურის ნომერი</label>
                <input 
                placeholder="+995 551 12 34 56"
                className="input-email"
                type="number" 
                name="number"
                value={value.number}
                {...register('number',{onChange:(e)=>handleChange(e)})}
                />
                <p className="name-criteria">უნდა აკმაყოფილებდეს ქართული მობილურის ნომრის ფორმატს</p>
                <button
                onClick={reset}
                type="submit"
                className="button">შემდეგი</button>
            </form>
            </div>
            <div className="cv-wraper">
                <div>
                    <h1>{value.firstName}{value.lastName}</h1>
                    <h5>{value.email}</h5>
                    <h5>{value.number}</h5>
                    <h2></h2>
                    <p>{value.textarea}</p>
               </div>
               { imgURL.map(imageSrc => <img style={{width:200,height:200,objectFit:'contain'}} src={imageSrc} />) }
            </div>
            <div></div>
        </div>
    )
}
