import React, { useEffect } from "react";

export default function Storage(props){

    useEffect(() => {
        const data = window.localStorage.getItem('MY_APP_STATE');
        if ( data !== null ) props.setValues(JSON.parse(data));props.setState(JSON.parse(data));
      }, []);
    useEffect(() => {
        window.localStorage.setItem('MY_APP_STATE', JSON.stringify(props.value,props.state));
      }, [props.value,props.state]);
    return(
        <></>
    )
}
