import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { useForm } from 'react-hook-form'
import Header from "./Header";


export default function Step1(){
    const { register, handleSubmit, watch, formState: { errors } } = useForm();
    const nav=useNavigate();
    const[value,setValue]=useState({
        firstName:'',
        lastName:'',
        textarea:'',
        email:'',
        number:''
})
    const handleChange=(e)=>{
        console.log(e.target.value)
        const name=e.target.name
        const val=e.target.value
        setValue({...value,[name]:val})
    }
    const onClick=()=>{
        nav('/step2',{state:{
            firstName:value.firstName,
            lastName:value.lastName,
            textarea:value.textarea,
            email:value.email,
            number:value.number
        }})
    }
    return(
        <div>
            <Header heading={location.pathname==='/Step1'?'პირადი ინფო':location.pathname==='/Step2'?'გამოცდილება':location.pathname==='/Step3'?'განათლება' :''}/>
            <form className="form" onSubmit={handleSubmit(onClick)}>
                <label>სახელი</label>
                <input
                name="firstName"
                defaultValue={value.firstName}
                 type="text" 
                 {...register('firstName',{onChange:(e)=>handleChange(e)})}
                 />
                <label>გვარი</label>
                <input 
                name="lastName"
                value={value.lastName}
                type="text"
                {...register('lastName',{onChange:(e)=>handleChange(e)})}
                />
                <label>პირადი ფოტოს ატვირთვა</label>
                <input 
                type="file"
                name="image" 
                {...register('image',{onChange:(e)=>handleChange(e)})}
                />
                <img 
                src='' 
                alt="" 
                />
                <label>ჩემს შესახებ (არასავალდებულო)</label>
                <input 
                type="textarea" 
                name="textarea"
                value={value.textarea}
                {...register('textarea',{onChange:(e)=>handleChange(e)})}
                />
                <label>ელ-ფოსტა</label>
                <input 
                type="email"
                name="email" 
                // value={value.email}
                {...register('email',{onChange:(e)=>handleChange(e)})}
                />
                <label>მობილურის ნომერი</label>
                <input 
                type="number" 
                name="number"
                // value={value.number}
                {...register('number',{onChange:(e)=>handleChange(e)})}
                />
                <button
                type="submit"
                className="button">შემდეგი</button>
            </form>
            <li>{value.firstName}</li>
            <li>{value.lastName}</li>
            <li>{value.textarea}</li>
            <li>{value.email}</li>
            <li>{value.number}</li>
        </div>
    )
}
