import React, { useEffect } from "react";

export default function Storage({value,state,}){

    useEffect(() => {
        const data = window.localStorage.getItem('MY_APP_STATE');
        if ( data !== null ) setValues(JSON.parse(data));setState(JSON.parse(data));
      }, []);
    useEffect(() => {
        window.localStorage.setItem('MY_APP_STATE', JSON.stringify(value,state));
      }, [value,state]);
    return(
        <></>
    )
}
